            return Optional.of(Integer.MAX_VALUE);
        case Errors.UNKNOWN_SUBSCRIPTION_ID:
        case Errors.UNSUPPORTED_COMPRESSION_TYPE:
            log.warn("Unknown subscription ID or unsupported compression type. Setting push interval to 0.");
            return Optional.of(0);
        case Errors.TELEMETRY_TOO_LARGE:
        case Errors.THROTTLING_QUOTA_EXCEEDED:
            log.warn("Telemetry too large or throttling quota exceeded. Setting push interval to either the provided interval or the default interval.");
            return Optional.of(intervalMs);
        default:
            log.error("Unknown error code. Setting push interval to Integer.MAX_VALUE.");
            return Optional.of(Integer.MAX_VALUE);
    }
