@Override
public void assign(Collection<TopicPartition> partitions) {
    // Acquire the consumer lock
    lock.lock();
    try {
        // Check if the collection is null or empty
        if (partitions == null || partitions.isEmpty()) {
            throw new IllegalArgumentException("The collection of TopicPartitions cannot be null or empty");
        }

        // Validate the topic names
        for (TopicPartition partition : partitions) {
            if (partition.topic() == null || partition.topic().isEmpty()) {
                throw new IllegalArgumentException("The topic name cannot be null or empty");
            }
        }

        // Clear the fetch buffer of partitions not in the new assignment
        Set<TopicPartition> currentAssignment = new HashSet<>(this.assignment);
        currentAssignment.removeAll(partitions);
        for (TopicPartition partition : currentAssignment) {
            fetchBuffer.remove(partition);
        }

        // Trigger an assignment change event
        assignmentChanged = true;
    } finally {
        // Release the consumer lock
        lock.unlock();
    }
}
