public static void writePropertiesFile(Properties props, String path, boolean fsync) throws IOException {
    // Create a temporary file to write the properties to
    File tempFile = File.createTempFile("properties", null);
    tempFile.deleteOnExit();

    // Write the properties to the temporary file
    try (FileOutputStream out = new FileOutputStream(tempFile)) {
        props.store(out, null);
    }

    // Atomically move the temporary file to the target file
    Utils.atomicMoveWithFallback(tempFile, new File(path));

    // If fsync is true, sync the file to the disk
    if (fsync) {
        FileChannel channel = new FileInputStream(path).getChannel();
        channel.force(true);
        channel.close();
    }
}
