public String encrypt(byte[] plainText) {
    // Generate a random salt
    byte[] salt = new byte[16];
    new SecureRandom().nextBytes(salt);

    // Generate an initialization vector (IV)
    byte[] iv = new byte[16];
    new SecureRandom().nextBytes(iv);

    // Generate a secret key from the password
    SecretKeySpec secretKey = new SecretKeySpec(salt, "AES");

    // Encrypt the data using AES-GCM
    Cipher cipher = Cipher.getInstance("AES/GCM/NoPadding");
    cipher.init(Cipher.ENCRYPT_MODE, secretKey, new GCMParameterSpec(128, iv));
    byte[] cipherText = cipher.doFinal(plainText);

    // Prefix the IV and salt to the cipher text
    byte[] cipherTextWithIvAndSalt = new byte[iv.length + salt.length + cipherText.length];
    System.arraycopy(iv, 0, cipherTextWithIvAndSalt, 0, iv.length);
    System.arraycopy(salt, 0, cipherTextWithIvAndSalt, iv.length, salt.length);
    System.arraycopy(cipherText, 0, cipherTextWithIvAndSalt, iv.length + salt.length, cipherText.length);

    // Encode the cipher text into a URL-safe base64 string
    return Base64.getUrlEncoder().encodeToString(cipherTextWithIvAndSalt);
}
