@Override
public void process(String base64AuthenticatorData, String signature, String clientDataJson, Fido2RegistrationData registration,
                        Fido2AuthenticationData authenticationEntity) {
    // Decode the base64 authenticator data
    byte[] authenticatorData = Base64.getDecoder().decode(base64AuthenticatorData);

    // Verify the registration domain
    if (!registration.getDomain().equals(authenticationEntity.getDomain())) {
        throw new Fido2CompromisedDevice("Registration domain does not match authentication domain");
    }

    // Verify the user verification option
    if (authenticationEntity.getUserVerification()!= registration.getUserVerification()) {
        throw new Fido2CompromisedDevice("User verification option does not match registration option");
    }

    // Verify the counter
    if (authenticationEntity.getCounter() <= registration.getCounter()) {
        throw new Fido2CompromisedDevice("Counter value is not greater than the registration counter");
    }

    // Verify the assertion signature
    if (!authenticationEntity.getSignature().equals(signature)) {
        throw new Fido2CompromisedDevice("Assertion signature does not match registration signature");
    }

    // Update the counter
    registration.setCounter(authenticationEntity.getCounter());
}
