public static String relativePath(Path absolutePath, Path basePath) {
    // Check if both paths are absolute
    if (!absolutePath.isAbsolute() ||!basePath.isAbsolute()) {
        throw new IllegalArgumentException("Both paths must be absolute");
    }

    // Get the common prefix of both paths
    Path commonPrefix = absolutePath.getRoot();
    for (int i = 0; i < absolutePath.getNameCount(); i++) {
        if (i >= basePath.getNameCount() ||!absolutePath.getName(i).equals(basePath.getName(i))) {
            break;
        }
        commonPrefix = commonPrefix.resolve(absolutePath.getName(i));
    }

    // Calculate the relative path
    Path relativePath = absolutePath.subpath(commonPrefix.getNameCount(), absolutePath.getNameCount());

    // Return the relative path
    return relativePath.toString();
}
