public static CacheControlHeader fromExpirationTime(String expirationTime) {
    CacheControlHeader cacheControlHeader = new CacheControlHeader();
    try {
        String[] timeUnits = expirationTime.split(" ");
        for (String timeUnit : timeUnits) {
            String[] timeUnitParts = timeUnit.split("(?<=\\D)(?=\\d)");
            int timeValue = Integer.parseInt(timeUnitParts[0]);
            char timeUnitChar = timeUnitParts[1].charAt(0);
            switch (timeUnitChar) {
                case's':
                    cacheControlHeader.setSeconds(timeValue);
                    break;
                case'm':
                    cacheControlHeader.setMinutes(timeValue);
                    break;
                case 'h':
                    cacheControlHeader.setHours(timeValue);
                    break;
                case 'd':
                    cacheControlHeader.setDays(timeValue);
                    break;
                default:
                    throw new IllegalArgumentException("Invalid time unit: " + timeUnitChar);
            }
        }
    } catch (NumberFormatException | IllegalArgumentException e) {
        logger.warn("Invalid expiration time: " + expirationTime);
        cacheControlHeader = new CacheControlHeader();
    }
    return cacheControlHeader;
}
