public String[] listFiles(URI fileUri, boolean recursive) throws IOException {
    // Initialize the S3 client
    AmazonS3 s3Client = AmazonS3ClientBuilder.defaultClient();

    // Set the pagination parameters
    int maxKeys = 1000;
    String continuationToken = null;

    // Create a list to store the file URIs
    List<String> fileUris = new ArrayList<>();

    // Loop until all files have been listed
    do {
        // Create a list objects request
        ListObjectsRequest request = new ListObjectsRequest()
               .withBucketName(fileUri.getHost())
               .withPrefix(fileUri.getPath())
               .withMaxKeys(maxKeys)
               .withContinuationToken(continuationToken);

        // Execute the request and get the result
        ListObjectsResult result = s3Client.listObjects(request);

        // Add the file URIs to the list
        for (S3ObjectSummary summary : result.getObjectSummaries()) {
            fileUris.add(summary.getKey());
        }

        // Set the continuation token for the next iteration
        continuationToken = result.getNextContinuationToken();
    } while (continuationToken!= null);

    // Return the list of file URIs
    return fileUris.toArray(new String[0]);
}
