private String saveAndChecksumBlockList(String jobName, LazyBlockList blockList) {
    // Save the block list to Google Cloud Storage
    String blockListPath = "gs://" + jobName + "/blocks.txt";
    try (OutputStream outputStream = new FileOutputStream(blockListPath)) {
        blockList.writeTo(outputStream);
    } catch (IOException e) {
        throw new RuntimeException("Failed to save block list to Google Cloud Storage", e);
    }

    // Compute the checksum of the block list
    String checksum;
    try {
        MessageDigest digest = MessageDigest.getInstance("SHA-256");
        byte[] bytes = blockList.toByteArray();
        digest.update(bytes, 0, bytes.length);
        checksum = Base64.getEncoder().encodeToString(digest.digest());
    } catch (NoSuchAlgorithmException e) {
        throw new RuntimeException("Failed to compute checksum of block list", e);
    }

    return checksum;
}
