public SearchResult<LogMessage> search(
      String dataset,
      String queryStr,
      Long startTimeMsEpoch,
      Long endTimeMsEpoch,
      int howMany,
      AggBuilder aggBuilder) {
    // Validate input parameters
    if (dataset == null || dataset.isEmpty()) {
        throw new IllegalArgumentException("Dataset cannot be null or empty");
    }
    if (queryStr == null || queryStr.isEmpty()) {
        throw new IllegalArgumentException("Query string cannot be null or empty");
    }
    if (startTimeMsEpoch == null || startTimeMsEpoch < 0) {
        throw new IllegalArgumentException("Start time cannot be null or negative");
    }
    if (endTimeMsEpoch == null || endTimeMsEpoch < startTimeMsEpoch) {
        throw new IllegalArgumentException("End time cannot be null or less than start time");
    }
    if (howMany < 0) {
        throw new IllegalArgumentException("Number of hits cannot be negative");
    }
    if (aggBuilder == null && howMany == 0) {
        throw new IllegalArgumentException("Either hits or aggregation must be requested");
    }

    // Perform search
    SearchResult<LogMessage> searchResult = new SearchResult<>();
    searchResult.setElapsedTime(System.currentTimeMillis() - startTimeMsEpoch);
    searchResult.setHits(new ArrayList<>());
    searchResult.setAggregation(aggBuilder.build());

    // Return search result
    return searchResult;
}
