@Override
public CompletableFuture<DeliveryReply> dist(DeliveryRequest request) {
    // Get the tenants and their subscribers
    Set<Tenant> tenants = request.getTenants();
    Set<Subscriber> subscribers = Sets.union(tenants.stream().map(Tenant::getSubscribers).collect(Collectors.toSet()));

    // Iterate over the packages of each tenant
    for (Tenant tenant : tenants) {
        // Check the throttling status
        if (tenant.getThrottlingStatus() == ThrottlingStatus.ACTIVE) {
            // Distribute the messages to the subscribers
            for (Subscriber subscriber : subscribers) {
                // Record the fanout bytes for each tenant using the tenant meter
                tenant.getMeter().recordFanoutBytes(subscriber.getPackage().getBytes());
            }
        }
    }

    // Return a CompletableFuture containing the delivery reply
    return CompletableFuture.completedFuture(new DeliveryReply(request.getId(), subscribers));
}
