public Range<BigDecimal> calcInterquartileRangeApproximation(List<BigDecimal> sortedValues) {
    // Calculate the IQR approximation
    BigDecimal q1 = sortedValues.get(sortedValues.size() / 4);
    BigDecimal q3 = sortedValues.get(sortedValues.size() * 3 / 4);
    BigDecimal iqr = q3.subtract(q1);

    // If the IQR is 0 and the maximum value is equal to Q3, try to find a non-zero IQR approximation by filtering out the maximum value and trying again
    if (iqr.compareTo(BigDecimal.ZERO) == 0 && q3.compareTo(sortedValues.get(sortedValues.size() - 1)) == 0) {
        sortedValues.remove(sortedValues.size() - 1);
        return calcInterquartileRangeApproximation(sortedValues);
    }

    // If the IQR is still empty or null, return a Range object with Q1 and Q3 as the endpoints
    if (iqr.compareTo(BigDecimal.ZERO) <= 0) {
        return Range.closedOpen(q1, q3);
    }

    // Return the IQR approximation
    return Range.closedOpen(q1.subtract(iqr), q3.add(iqr));
}
