public void add(List<Document> documents) {
    if (documents == null || documents.isEmpty()) {
        return;
    }

    List<SearchDocument> searchDocuments = documents.stream()
            .map(document -> {
                SearchDocument searchDocument = new SearchDocument();
                searchDocument.setId(document.getId());
                searchDocument.setEmbeddings(document.getEmbeddings());
                searchDocument.setContent(document.getContent());
                searchDocument.setMetadata(document.getMetadata());
                searchDocument.setFilterableMetadata(document.getFilterableMetadata());
                return searchDocument;
            })
            .collect(Collectors.toList());

    searchClient.uploadDocuments(searchDocuments);

    for (SearchDocument searchDocument : searchDocuments) {
        if (!searchClient.getIndexingStatus(searchDocument.getId()).isSuccessful()) {
            throw new IllegalArgumentException("Indexing operation was not successful.");
        }
    }
}
