public int migration(String srcTable, List<String> uuids, String targetTable)
        throws SwValidationException {
    if (srcTable == null || srcTable.isEmpty() || targetTable == null || targetTable.isEmpty()) {
        throw new SwValidationException("Source table or target table cannot be null or empty.");
    }

    int result = 0;
    try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD);
            Statement stmt = conn.createStatement()) {
        String query = "SELECT * FROM " + srcTable + " WHERE uuid IN (" + String.join(",", uuids) + ")";
        ResultSet rs = stmt.executeQuery(query);
        while (rs.next()) {
            String uuid = rs.getString("uuid");
            String name = rs.getString("name");
            String email = rs.getString("email");
            String phone = rs.getString("phone");
            String address = rs.getString("address");
            String city = rs.getString("city");
