            String insertQuery = "INSERT INTO " + targetTable + " (uuid, name, email, phone, address, city, state, zip, country, website, company, job_title, industry, about, created_at, updated_at, deleted_at) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";
