public static List<TableCheckVo> checkTablesWithFilter(Endpoint endpoint, String nameFilter) {
    List<TableCheckVo> tableCheckVos = Lists.newLinkedList();
    if (StringUtils.isEmpty(nameFilter)) {
        nameFilter = MATCH_ALL_FILTER;
    }
    for (Table table : endpoint.getTables()) {
        if (table.getName().matches(nameFilter)) {
            tableCheckVos.add(new TableCheckVo(table.getName(), true));
        } else {
            tableCheckVos.add(new TableCheckVo(table.getName(), false));
        }
    }
    return tableCheckVos;
}
