public static String getExpandUrl(String url, Object paramObj) {
    UriComponentsBuilder builder = UriComponentsBuilder.fromHttpUrl(url);
    if (paramObj instanceof Map) {
        Map<String, String> params = (Map<String, String>) paramObj;
        for (Map.Entry<String, String> entry : params.entrySet()) {
            builder.queryParam(entry.getKey(), entry.getValue());
        }
    } else {
        for (Field field : paramObj.getClass().getDeclaredFields()) {
            field.setAccessible(true);
            builder.queryParam(field.getName(), field.get(paramObj));
        }
    }
    return builder.build().toUriString();
}
