@Override
public S3Resource upload(String bucketName, String key, InputStream inputStream,
        @Nullable ObjectMetadata objectMetadata) {
    if (bucketName == null || key == null || inputStream == null) {
        throw new IllegalArgumentException("Required parameters are null");
    }
    try {
        PutObjectRequest putObjectRequest = PutObjectRequest.builder()
                .bucket(bucketName)
                .key(key)
                .contentLength(inputStream.available())
                .contentType("application/octet-stream")
                .build();
        PutObjectResponse putObjectResponse = s3Client.putObject(putObjectRequest,
                RequestBody.fromInputStream(inputStream, inputStream.available()));
        return new S3Resource(putObjectResponse.sdkHttpResponse().headers());
    } catch (SdkClientException e) {
        throw new S3Exception("Error uploading object", e);
    }
}
