@Override
public void collect(CollectRep.MetricsData.Builder builder, long monitorId, String app, Metrics metrics)
        throws IOException {
    // Validate the parameters
    if (builder == null) {
        throw new IllegalArgumentException("The builder object cannot be null.");
    }
    if (monitorId <= 0) {
        throw new IllegalArgumentException("The monitor ID must be a positive integer.");
    }
    if (app == null || app.isEmpty()) {
        throw new IllegalArgumentException("The application name cannot be null or empty.");
    }
    if (metrics == null) {
        throw new IllegalArgumentException("The metrics object cannot be null.");
    }

    // Create an HTTP request and context
    HttpClient client = HttpClientBuilder.create().build();
    HttpGet request = new HttpGet(metrics.getUrl());
    HttpContext context = new BasicHttpContext();

    // Execute the request
    try {
        HttpResponse response = client.execute(request, context);
        int statusCode = response.getStatusLine().getStatusCode();
        if (statusCode != HttpStatus.SC_OK) {
            throw new IOException("The HTTP request failed with status code " + statusCode);
        }

        // Parse the response based on the metrics name
        String responseBody = EntityUtils.toString(response.getEntity(), StandardCharsets.UTF_8);
        switch (metrics.getName()) {
            case "CPU":
                builder.setCpu(Double.parseDouble(responseBody));
                break;
            case "Memory":
                builder.setMemory(Long.parseLong(responseBody));
                break;
            case "Disk":
                builder.setDisk(Long.parseLong(responseBody));
                break;
            default:
                throw new IllegalArgumentException("The metrics name is not supported.");
        }
    } catch (IOException e) {
        builder.setResponseCode(CollectRep.MetricsData.ResponseCode.FAIL);
        builder.setResponseMessage(e.getMessage());
    }

    // Abort the HTTP request if it was created successfully
    if (request != null) {
        request.abort();
    }
}
