public ListenableFuture<Void> queue(ExecuteEntry executeEntry, Poller poller, Duration timeout) {
    ExecuteOperationMetadata metadata = ExecuteOperationMetadata.newBuilder()
            .setActionKey(executeEntry.getActionKey())
            .setOperation(executeEntry.getOperation())
            .build();
    Operation operation = Operation.newBuilder()
            .setMetadata(Any.pack(metadata))
            .build();
    String actionKey = executeEntry.getActionKey();
    String operationName = executeEntry.getOperation().getName();
    Stopwatch stopwatch = Stopwatch.createStarted();
    ListenableFuture<Void> future = poller.queue(operation, timeout);
    if (cache.containsKey(actionKey)) {
        stopwatch.stop();
        LOGGER.info("Cached result for actionKey {} and operation {}", actionKey, operationName);
        poller.pause();
    } else {
        cache.put(actionKey, operation);
        poller.queue(operation, timeout);
    }
    return future;
}
