    if (allowedBPMNProcessIds != null && !allowedBPMNProcessIds.isEmpty()) {
        BoolQueryBuilder boolQueryBuilder = QueryBuilders.boolQuery();
        for (String allowedBPMNProcessId : allowedBPMNProcessIds) {
            boolQueryBuilder.should(QueryBuilders.matchQuery("bpmnProcessId", allowedBPMNProcessId));
        }
        searchSourceBuilder.query(boolQueryBuilder);
    }
    searchSourceBuilder.aggregation(AggregationBuilders.terms("tenantId").field("tenantId"));
    searchSourceBuilder.aggregation(AggregationBuilders.terms("bpmnProcessId").field("bpmnProcessId"));
    searchSourceBuilder.aggregation(AggregationBuilders.topHits("topHits").sort("version", SortOrder.DESC));
    SearchRequest searchRequest = new SearchRequest("processes");
    searchRequest.source(searchSourceBuilder);
    SearchResponse searchResponse = elasticsearchClient.search(searchRequest, RequestOptions.DEFAULT);
    for (Terms.Bucket tenantIdBucket : searchResponse.getAggregations().get("tenantId").getBuckets()) {
        String tenantId = tenantIdBucket.getKeyAsString();
        for (Terms.Bucket bpmnProcessIdBucket : tenantIdBucket.getAggregations().get("bpmnProcessId").getBuckets()) {
            String bpmnProcessId = bpmnProcessIdBucket.getKeyAsString();
