Map<String, Set<Pair<TableRebalanceContext, Long>>> getCandidateJobs(String tableNameWithType,
      Map<String, Map<String, String>> allJobMetadata) throws Exception {
    Map<String, Set<Pair<TableRebalanceContext, Long>>> candidateJobs = new HashMap<>();
    for (String jobId : allJobMetadata.keySet()) {
        Map<String, String> jobMetadata = allJobMetadata.get(jobId);
        if (jobMetadata == null) {
            continue;
        }
        String jobStatus = jobMetadata.get(JobMetadataKey.STATUS);
        if (jobStatus == null) {
            continue;
        }
        if (jobStatus.equals(JobStatus.COMPLETED.name()) || jobStatus.equals(JobStatus.RUNNING.name())
                || jobStatus.equals(JobStatus.CANCELLED.name())) {
            continue;
        }
        String jobType = jobMetadata.get(JobMetadataKey.TYPE);
        if (jobType == null) {
            continue;
        }
        if (!jobType.equals(JobType.REBALANCE.name())) {
            continue;
        }
        String jobTableName = jobMetadata.get(JobMetadataKey.TABLE_NAME);
        if (jobTableName == null) {
            continue;
        }
        if (!jobTableName.equals(tableNameWithType)) {
            continue;
        }
        String jobStartTime = jobMetadata.get(JobMetadataKey.START_TIME);
        if (jobStartTime == null) {
            continue;
        }
        long startTime = Long.parseLong(jobStartTime);
        String jobContext = jobMetadata.get(JobMetadataKey.CONTEXT);
        if (jobContext == null) {
            continue;
        }
        TableRebalanceContext context = TableRebalanceContext.fromJson(jobContext);
        if (context == null) {
            continue;
        }
        if (context.getTableName().equals(tableNameWithType)) {
            Set<Pair<TableRebalanceContext, Long>> contexts = candidateJobs.get(jobId);
            if (contexts == null) {
                contexts = new HashSet<>();
                candidateJobs.put(jobId, contexts);
            }
            contexts.add(new Pair<>(context, startTime));
        }
    }
    return candidateJobs;
}
