@Override
public CompletableFuture<RemotingCommand> sendMessageBack(ProxyContext ctx, ReceiptHandle handle, String messageId,
        ConsumerSendMsgBackRequestHeader requestHeader, long timeoutMillis) {
    if (requestHeader.getDelayLevel() == null || requestHeader.getOffset() == null) {
        RemotingCommand response = RemotingCommand.createResponseCommand(SendMessageResponseHeader.class);
        response.setCode(ResponseCode.SYSTEM_ERROR);
        response.setRemark("Delay level and offset cannot be null");
        return CompletableFuture.completedFuture(response);
    }

    String topic = requestHeader.getTopic();
    String group = requestHeader.getConsumerGroup();

    if (!topic.equals(handle.getTopic())) {
        log.error("Topic id in request header does not match topic id in message queue");
        throw new ProxyException("Topic id in request header does not match topic id in message queue");
    }

    MessageStore store = ctx.getStore();
    Message message = store.getMessage(group, topic, requestHeader.getOffset());

    if (message == null) {
        log.error("Message not found");
        throw new ProxyException("Message not found");
    }

    int deliveryAttempts = message.getDeliveryAttempts();
    if (deliveryAttempts > maxDeliveryAttempts) {
        log.error("Delivery attempts exceed maximum delivery attempt");
        throw new ProxyException("Delivery attempts exceed maximum delivery attempt");
    }

    if (deliveryAttempts == maxDeliveryAttempts) {
        log.error("Delivery attempts exceed maximum delivery attempt");
        throw new ProxyException("Delivery attempts exceed maximum delivery attempt");
    }

    if (requestHeader.getDelayLevel() < 0) {
        log.error("Delay level is less than 0");
        throw new ProxyException("Delay level is less than 0");
    }

    if (requestHeader.getDelayLevel() == 0) {
        deliveryAttempts = message.getDeliveryAttempts();
        message.setDeliveryAttempts(deliveryAttempts + 1);
        message.setOriginalQueueOffset(requestHeader.getOffset());
    }

    if (requestHeader.getDelayLevel() > 0) {
        message.setDeliveryAttempts(deliveryAttempts + 1);
        message.setOriginalQueueOffset(requestHeader.getOffset());
    }

    RemotingCommand response = RemotingCommand.createResponseCommand(SendMessageResponseHeader.class);
    response.setCode(ResponseCode.SUCCESS);
    response.setRemark("Success");
    return CompletableFuture.completedFuture(response);
}
