import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.Enumeration;
import java.util.zip.GZIPInputStream;
import java.util.zip.ZipEntry;
import java.util.zip.ZipFile;

import org.apache.commons.compress.archivers.tar.TarArchiveEntry;
import org.apache.commons.compress.archivers.tar.TarArchiveInputStream;
import org.apache.commons.compress.compressors.gzip.GzipCompressorInputStream;
import org.apache.commons.io.IOUtils;

public class UnTarZip {

    public static void unTarZip(String tarFilePath, String targetDirectoryPath) {
        try {
            ZipFile zipFile = new ZipFile(tarFilePath);
            Enumeration<? extends ZipEntry> entries = zipFile.entries();
            while (entries.hasMoreElements()) {
                ZipEntry entry = entries.nextElement();
                if (!entry.isDirectory()) {
                    String entryName = entry.getName();
                    String[] entryNameParts = entryName.split("/");
                    String fileName = entryNameParts[entryNameParts.length - 1];
                    String parentDirectory = entryName.substring(0, entryName.length() - fileName.length() - 1);
                    Path parentDirectoryPath = Paths.get(targetDirectoryPath, parentDirectory);
                    if (!Files.exists(parentDirectoryPath)) {
                        Files.createDirectories(parentDirectoryPath);
                    }
                    Path filePath = Paths.get(targetDirectoryPath, parentDirectory, fileName);
                    try (InputStream inputStream = zipFile.getInputStream(entry);
                            FileOutputStream fileOutputStream = new FileOutputStream(filePath.toFile())) {
                        IOUtils.copy(inputStream, fileOutputStream);
                    }
                }
            }
        } catch (IOException e) {
            throw new CoreException("Error while decompressing tar.gz file", e);
        }
    }
}
