@Override
public String decrypt(String encryptedText) {
    // Decode the Base64 encoded encrypted text to get the byte array
    byte[] encryptedBytes = Base64.getDecoder().decode(encryptedText);

    // Extract the Initialization Vector (IV) and salt from the byte array
    byte[] iv = Arrays.copyOfRange(encryptedBytes, 0, 16);
    byte[] salt = Arrays.copyOfRange(encryptedBytes, 16, 24);

    // Extract the cipher text from the byte array
    byte[] cipherText = Arrays.copyOfRange(encryptedBytes, 24, encryptedBytes.length);

    // Generate the AES key from the password and salt
    SecretKeySpec aesKey = new SecretKeySpec(getAesKey(password, salt), "AES");

    // Initialize the cipher in decryption mode with the AES key and IV
    Cipher cipher = Cipher.getInstance("AES/GCM/NoPadding");
    cipher.init(Cipher.DECRYPT_MODE, aesKey, new GCMParameterSpec(128, iv));

    // Decrypt the cipher text to get the plain text
    byte[] plainText = cipher.doFinal(cipherText);

    // Convert the plain text byte array to a string using UTF-8 encoding
    return new String(plainText, StandardCharsets.UTF_8);
}
