@Override
public void assign(Collection<TopicPartition> partitions) {
    if (partitions == null || partitions.isEmpty()) {
        throw new IllegalArgumentException("partitions cannot be null or empty");
    }

    // acquire the consumer lock
    synchronized (this) {
        // clear the fetch buffer of partitions not in the new assignment
        for (TopicPartition partition : this.assignment) {
            if (!partitions.contains(partition)) {
                this.fetchBuffer.remove(partition);
            }
        }

        // validate the topic names
        for (TopicPartition partition : partitions) {
            if (partition.topic() == null || partition.topic().isEmpty()) {
                throw new IllegalArgumentException("partitions cannot contain null or empty topic names");
            }
        }

        // trigger an assignment change event
        this.assignment = partitions;
        this.assignmentChangeListener.onAssignmentChange(this.assignment);
    }
}
