public String encrypt(byte[] plainText) {
    try {
        // Generate a random salt
        SecureRandom random = SecureRandom.getInstanceStrong();
        byte[] salt = new byte[16];
        random.nextBytes(salt);

        // Generate a secret key from the password
        SecretKeyFactory factory = SecretKeyFactory.getInstance("PBKDF2WithHmacSHA256");
        KeySpec spec = new PBEKeySpec(password.toCharArray(), salt, 65536, 256);
        SecretKey tmp = factory.generateSecret(spec);
        SecretKey secret = new SecretKeySpec(tmp.getEncoded(), "AES");

        // Generate an initialization vector (IV)
        byte[] iv = new byte[16];
        random.nextBytes(iv);

        // Encrypt the plain text
        Cipher cipher = Cipher.getInstance("AES/GCM/NoPadding");
        cipher.init(Cipher.ENCRYPT_MODE, secret, new GCMParameterSpec(128, iv));
        byte[] cipherText = cipher.doFinal(plainText);

        // Prefix the IV and salt to the cipher text
        byte[] prefixedCipherText = new byte[iv.length + salt.length + cipherText.length];
        System.arraycopy(iv, 0, prefixedCipherText, 0, iv.length);
        System.arraycopy(salt, 0, prefixedCipherText, iv.length, salt.length);
        System.arraycopy(cipherText, 0, prefixedCipherText, iv.length + salt.length, cipherText.length);

        // Encode the prefixed cipher text to a URL-safe base64 string
        return Base64.getUrlEncoder().withoutPadding().encodeToString(prefixedCipherText);
    } catch (NoSuchAlgorithmException | InvalidKeySpecException | NoSuchPaddingException | InvalidKeyException | IllegalBlockSizeException | BadPaddingException e) {
        throw new NitriteSecurityException("Encryption failed", e);
    }
}
