        throw new Fido2CompromisedDevice("The device is compromised.");
    }

    // Verify the user verification option
    if (registration.getUserVerification() != authenticationEntity.getUserVerification()) {
        throw new Fido2CompromisedDevice("The device is compromised.");
    }

    // Verify the counter
    if (registration.getCounter() != authenticationEntity.getCounter()) {
        throw new Fido2CompromisedDevice("The device is compromised.");
    }

    // Verify the assertion signature
    if (!verifySignature(base64AuthenticatorData, signature, clientDataJson, registration)) {
        throw new Fido2CompromisedDevice("The device is compromised.");
    }
