public static CacheControlHeader fromExpirationTime(String expirationTime) {
    if (expirationTime == null) {
        logger.warn("Invalid expiration time string: null");
        return new CacheControlHeader();
    }

    String[] timeUnits = expirationTime.split(" ");
    if (timeUnits.length == 0) {
        logger.warn("Invalid expiration time string: " + expirationTime);
        return new CacheControlHeader();
    }

    long expirationTimeInSeconds = 0;
    for (String timeUnit : timeUnits) {
        if (timeUnit.length() != 1) {
            logger.warn("Invalid expiration time string: " + expirationTime);
            return new CacheControlHeader();
        }

        char timeUnitChar = timeUnit.charAt(0);
        long timeUnitValue = Long.parseLong(timeUnit);
        switch (timeUnitChar) {
            case 's':
                expirationTimeInSeconds += timeUnitValue;
                break;
            case 'm':
                expirationTimeInSeconds += timeUnitValue * 60;
                break;
            case 'h':
                expirationTimeInSeconds += timeUnitValue * 60 * 60;
                break;
            case 'd':
                expirationTimeInSeconds += timeUnitValue * 60 * 60 * 24;
                break;
            default:
                logger.warn("Invalid expiration time string: " + expirationTime);
                return new CacheControlHeader();
        }
    }

    return new CacheControlHeader(expirationTimeInSeconds);
}
