@Override
public ListenableFuture<Iterable<Digest>> findMissingBlobs(
      Iterable<Digest> blobDigests, RequestMetadata requestMetadata)
      throws IOException {
    // Check if the request is in the deny list.
    if (denyList.contains(requestMetadata.getRequestId())) {
      return Futures.immediateFailedFuture(
          new StatusException(Status.UNAVAILABLE));
    }

    // Filter out empty blobs from the list.
    Iterable<Digest> filteredBlobDigests =
        Iterables.filter(blobDigests, new Predicate<Digest>() {
          @Override
          public boolean apply(Digest digest) {
            return !digest.isEmpty();
          }
        });

    // If all blobs are empty, return an immediate future with an empty list.
    if (Iterables.isEmpty(filteredBlobDigests)) {
      return Futures.immediateFuture(Collections.<Digest>emptyList());
    }

    // If the server configuration allows finding missing blobs via backplane,
    // call the findMissingBlobsViaBackplane method.
    if (allowBackplane) {
      return findMissingBlobsViaBackplane(filteredBlobDigests,
          requestMetadata);
    }

    // Otherwise, call the findMissingBlobsQueryingEachWorker method.
    return findMissingBlobsQueryingEachWorker(filteredBlobDigests,
        requestMetadata);
  }
