private String saveAndChecksumBlockList(String jobName, LazyBlockList blockList) {
    try {
        // Save the block list to Google Cloud Storage.
        Storage storage = StorageOptions.getDefaultInstance().getService();
        BlobId blobId = BlobId.of(jobName, "block-list");
        BlobInfo blobInfo = BlobInfo.newBuilder(blobId).build();
        storage.create(blobInfo, blockList.getBlocks().stream().map(Block::getData).collect(Collectors.toList()));

        // Compute the checksum of the block list.
        MessageDigest digest = MessageDigest.getInstance("SHA-256");
        for (Block block : blockList.getBlocks()) {
            digest.update(block.getData());
        }
        byte[] checksum = digest.digest();

        // Return the base16 encoded checksum of the block list.
        return BaseEncoding.base16().encode(checksum);
    } catch (IOException | NoSuchAlgorithmException e) {
        throw new RuntimeException(e);
    }
}
