public SearchResult<LogMessage> search(
      String dataset,
      String queryStr,
      Long startTimeMsEpoch,
      Long endTimeMsEpoch,
      int howMany,
      AggBuilder aggBuilder) {

    // Check input parameters
    if (dataset == null || dataset.isEmpty()) {
      throw new IllegalArgumentException("Dataset name cannot be null or empty");
    }
    if (queryStr == null || queryStr.isEmpty()) {
      throw new IllegalArgumentException("Query string cannot be null or empty");
    }
    if (startTimeMsEpoch < 0) {
      throw new IllegalArgumentException("Start time cannot be negative");
    }
    if (endTimeMsEpoch < startTimeMsEpoch) {
      throw new IllegalArgumentException("End time cannot be less than start time");
    }
    if (howMany < 0) {
      throw new IllegalArgumentException("Number of hits requested cannot be negative");
    }
    if (aggBuilder == null && howMany == 0) {
      throw new IllegalArgumentException("Either hits or aggregation must be requested");
    }

    // Perform search
    SearchResult<LogMessage> result = new SearchResult<>();
    result.setElapsedTimeMs(System.currentTimeMillis() - startTimeMsEpoch);
    result.setAggregation(aggBuilder.build(result.getHits()));
    return result;
  }
