@Override
public CompletableFuture<DeliveryReply> dist(DeliveryRequest request) {
    // Get the tenants and their subscribers
    Set<Tenant> tenants = request.getTenants();
    Set<Subscriber> subscribers = request.getSubscribers();

    // Create a set of MatchInfo objects
    Set<MatchInfo> matchInfos = new HashSet<>();

    // Iterate over the packages of each tenant
    for (Tenant tenant : tenants) {
        // Check the throttling status
        if (tenant.getThrottlingStatus()) {
            // Distribute the messages to the subscribers
            for (Subscriber subscriber : subscribers) {
                // Create a MatchInfo object
                MatchInfo matchInfo = new MatchInfo(tenant, subscriber);

                // Add the MatchInfo object to the set
                matchInfos.add(matchInfo);
            }
        }
    }

    // Use the API of com.google.common.collect.Sets.union to combine the sets of MatchInfo objects
    Set<MatchInfo> union = Sets.union(matchInfos);

    // Create a DeliveryReply object
    DeliveryReply deliveryReply = new DeliveryReply(union);

    // Return the CompletableFuture containing the delivery reply
    return CompletableFuture.completedFuture(deliveryReply);
}
