public Response copyResponse(Response response) {
    if (response == null) {
        return null;
    }
    final Body body = response.body();
    if (body == null) {
        return response;
    }
    try {
        responseBody = Util.toByteArray(body.asInputStream());
    } catch (Exception ex) {
        LogManager.warn("feign.copyResponse", "uri: " + getUri(), ex);
    }
    if (body.isRepeatable()) {
        return response;
    }
