import torch

def s4d_kernel(A: torch.Tensor, B: torch.Tensor, C: torch.Tensor, dt: float, L: int) -> torch.Tensor:
    if not isinstance(dt, float):
        raise TypeError("dt must be a float.")
    if not isinstance(L, int):
        raise TypeError("L must be an integer.")
    if A.shape[0]!= B.shape[0] or A.shape[0]!= C.shape[0]:
        raise ValueError("The batch size of A, B, and C must be the same.")
    if A.shape[1]!= B.shape[1] or A.shape[1]!= C.shape[1]:
        raise ValueError("The dimension of A, B, and C must be the same.")

    batch_size, seqlen, dim = A.shape[0], L, A.shape[1]
    kernel = torch.zeros(batch_size, seqlen, dim)

    for i in range(seqlen):
        kernel[:, i, :] = torch.exp(A * dt * i) * B + C

    return kernel
