        .mapToObj(i -> new Pair<>(molecularProfileIds.get(i), caseIds.get(i)))
        .collect(groupingBy(
            Pair<String, String>::getFirst,
            mapping(Pair<String, String>::getSecond, toSet()))
        );
}

