public static String getExpandUrl(String url, Object paramObj) {
    UriComponentsBuilder builder = UriComponentsBuilder.fromHttpUrl(url);

    if (paramObj instanceof Map) {
        Map<?,?> map = (Map<?,?>) paramObj;
        for (Map.Entry<?,?> entry : map.entrySet()) {
            builder.queryParam(entry.getKey().toString(), entry.getValue().toString());
        }
    } else {
        Field[] fields = paramObj.getClass().getDeclaredFields();
        for (Field field : fields) {
            field.setAccessible(true);
            try {
                Object value = field.get(paramObj);
                if (value!= null) {
                    builder.queryParam(field.getName(), value.toString());
                }
            } catch (IllegalAccessException e) {
                e.printStackTrace();
            }
        }
    }

    return builder.build().toUriString();
}
```

