public Map<ProcessKey, List<ProcessEntity>> getProcessesGrouped(String tenantId, @Nullable Set<String> allowedBPMNProcessIds) {
    try {
        SearchRequest searchRequest = new SearchRequest(INDEX_NAME);
        SearchSourceBuilder searchSourceBuilder = new SearchSourceBuilder();

        // Filter by tenant ID
        BoolQueryBuilder tenantFilter = QueryBuilders.boolQuery().must(QueryBuilders.termQuery("tenantId", tenantId));

        // Filter by BPMN process ID if provided
        if (allowedBPMNProcessIds!= null &&!allowedBPMNProcessIds.isEmpty()) {
            BoolQueryBuilder bpmnFilter = QueryBuilders.boolQuery();
            for (String bpmnProcessId : allowedBPMNProcessIds) {
                bpmnFilter.should(QueryBuilders.termQuery("bpmnProcessId", bpmnProcessId));
            }
            tenantFilter.must(bpmnFilter);
        }

        searchSourceBuilder.query(tenantFilter);

        // Group by tenant ID and BPMN process ID
        TermsAggregationBuilder tenantAggregation = AggregationBuilders.terms("tenantId").field("tenantId.keyword");
        TermsAggregationBuilder bpmnAggregation = AggregationBuilders.terms("bpmnProcessId").field("bpmnProcessId.keyword");
        tenantAggregation.subAggregation(bpmnAggregation);

        // Further group by process ID and get the top hits sorted by version in descending order
        TopHitsAggregationBuilder topHitsAggregation = AggregationBuilders.topHits("top_hits").sort("version", SortOrder.DESC);
        bpmnAggregation.subAggregation(topHitsAggregation);

        searchSourceBuilder.aggregation(tenantAggregation);

        searchRequest.source(searchSourceBuilder);

        SearchResponse searchResponse = client.search(searchRequest, RequestOptions.DEFAULT);

        // Process the response and return the grouped processes
        Map<ProcessKey, List<ProcessEntity>> groupedProcesses = new HashMap<>();
        Terms terms = searchResponse.getAggregations().get("tenantId");
        for (Terms.Bucket tenantBucket : terms.getBuckets()) {
            String tenant = tenantBucket.getKeyAsString();
            Terms bpmnTerms = tenantBucket.getAggregations().get("bpmnProcessId");
            for (Terms.Bucket bpmnBucket : bpmnTerms.getBuckets()) {
                String bpmnProcessId = bpmnBucket.getKeyAsString();
                TopHits topHits = bpmnBucket.getAggregations().get("top_hits");
                for (SearchHit hit : topHits.getHits().getHits()) {
                    ProcessEntity processEntity = new ProcessEntity();
                    // Populate processEntity fields from hit.getSource()
                    ProcessKey processKey = new ProcessKey(tenant, bpmnProcessId);
                    groupedProcesses.computeIfAbsent(processKey, k -> new ArrayList<>()).add(processEntity);
                }
            }
        }

        return groupedProcesses;
    } catch (IOException e) {
        throw new OperateRuntimeException("Failed to retrieve grouped processes from Elasticsearch", e);
    }
}
