public<T> T clone(T object) {
    if (object instanceof String) {
        return object;
    } else if (object instanceof Collection) {
        Collection<?> collection = (Collection<?>) object;
        for (Object element : collection) {
            if (element!= null) {
                if (element instanceof Serializable) {
                    return (T) SerializationHelper.clone(element);
                } else {
                    return (T) ObjectMapper.clone(collection);
                }
            }
        }
    } else if (object instanceof Map) {
        Map<?,?> map = (Map<?,?>) object;
        for (Map.Entry<?,?> entry : map.entrySet()) {
            if (entry.getKey()!= null && entry.getValue()!= null) {
                if (entry.getKey() instanceof Serializable && entry.getValue() instanceof Serializable) {
                    return (T) SerializationHelper.clone(map);
                } else {
                    return (T) ObjectMapper.clone(map);
                }
            }
        }
    } else if (object instanceof JsonNode) {
        return (T) JsonNode.deepCopy((JsonNode) object);
    } else if (object instanceof Serializable) {
        return (T) SerializationHelper.clone(object);
    } else {
        return (T) JsonMarshallingStrategy.clone(object);
    }
    return null;
}
