@VisibleForTesting
static Map<String, Set<Pair<TableRebalanceContext, Long>>> getCandidateJobs(String tableNameWithType,
      Map<String, Map<String, String>> allJobMetadata) throws Exception {
    Map<String, Set<Pair<TableRebalanceContext, Long>>> candidateJobs = new HashMap<>();
    for (Map.Entry<String, Map<String, String>> jobEntry : allJobMetadata.entrySet()) {
        String jobId = jobEntry.getKey();
        Map<String, String> jobMetadata = jobEntry.getValue();
        String jobStatus = jobMetadata.get("status");
        if (jobStatus!= null &&!jobStatus.equals("COMPLETED") &&!jobStatus.equals("RUNNING") &&!jobStatus.equals("CANCELLED")) {
            String jobTableNameWithType = jobMetadata.get("tableNameWithType");
            if (jobTableNameWithType!= null && jobTableNameWithType.equals(tableNameWithType)) {
                String jobStartTime = jobMetadata.get("startTime");
                if (jobStartTime!= null) {
                    long startTimeMillis = Long.parseLong(jobStartTime);
                    TableRebalanceContext context = new TableRebalanceContext(jobMetadata);
                    Pair<TableRebalanceContext, Long> jobPair = new Pair<>(context, startTimeMillis);
                    if (candidateJobs.containsKey(jobId)) {
                        candidateJobs.get(jobId).add(jobPair);
                    } else {
                        Set<Pair<TableRebalanceContext, Long>> jobSet = new HashSet<>();
                        jobSet.add(jobPair);
                        candidateJobs.put(jobId, jobSet);
                    }
                }
            }
        }
    }
    return candidateJobs;
}
