        ConsumerSendMsgBackRequestHeader requestHeader, long timeoutMillis) {
    RemotingCommand response = RemotingCommand.createResponseCommand(RemotingSysResponseCode.SUCCESS, "");

    if (requestHeader.getDelayLevel() == null || requestHeader.getOffset() == null) {
        response.setCode(RemotingSysResponseCode.SYSTEM_ERROR);
        response.setRemark("Delay level or offset is null");
        return CompletableFuture.completedFuture(response);
    }

    String topic = requestHeader.getTopic();
    String group = requestHeader.getGroup();

    if (!topic.equals(messageId)) {
        log.error("Topic id in the request header does not match the topic id in the message queue");
        throw new ProxyException("Topic id in the request header does not match the topic id in the message queue");
    }

    MessageExt message = store.pullMessage(group, topic, requestHeader.getOffset());

    if (message!= null) {
        if (message.getDeliveryAttempt() > MAX_DELIVERY_ATTEMPT) {
            deadLetterService.sendMessageToDeadLetter(message);
        } else {
            if (requestHeader.getDelayLevel() < 0) {
                deadLetterService.sendMessageToDeadLetter(message);
            } else if (requestHeader.getDelayLevel() == 0) {
                int deliveryAttempt = message.getDeliveryAttempt() + 1;
                message.setDeliveryAttempt(deliveryAttempt);
                message.setOriginalQueueOffset(requestHeader.getOffset());
            } else {
                int deliveryAttempt = message.getDeliveryAttempt() + 1;
                message.setDeliveryAttempt(deliveryAttempt);
                message.setOriginalQueueOffset(requestHeader.getOffset());
            }
        }
    }

    if (response.getCode()!= RemotingSysResponseCode.SUCCESS) {
        response.setRemark("An error occurred while sending the message back");
    }

    return CompletableFuture.completedFuture(response);
}
