public static void unTarZip(String tarFilePath, String targetDirectoryPath) throws CoreException {
    File tarFile = new File(tarFilePath);
    File targetDirectory = new File(targetDirectoryPath);

    if (!targetDirectory.exists()) {
        throw new CoreException("Target directory does not exist: " + targetDirectoryPath);
    }

    if (!targetDirectory.isDirectory()) {
        throw new CoreException("Target path is not a directory: " + targetDirectoryPath);
    }

    try (TarArchiveInputStream tarInput = new TarArchiveInputStream(new GzipCompressorInputStream(new FileInputStream(tarFile)))) {
        TarArchiveEntry entry;
        while ((entry = tarInput.getNextTarEntry())!= null) {
            File outputFile = new File(targetDirectory, entry.getName());
            if (entry.isDirectory()) {
                if (!outputFile.exists()) {
                    outputFile.mkdirs();
                }
            } else {
                File parentDir = outputFile.getParentFile();
                if (!parentDir.exists()) {
                    parentDir.mkdirs();
                }
                try (OutputStream outputStream = new FileOutputStream(outputFile)) {
                    IOUtils.copy(tarInput, outputStream);
                }
            }
        }
    } catch (IOException e) {
        throw new CoreException("Error while decompressing tar.gz file", e);
    }
}
