public String decrypt(String encryptedText) throws NitriteSecurityException {
    try {
        byte[] encryptedBytes = Base64.getDecoder().decode(encryptedText);

        byte[] iv = new byte[12];
        byte[] salt = new byte[16];
        byte[] cipherText = new byte[encryptedBytes.length - 28];

        System.arraycopy(encryptedBytes, 0, iv, 0, 12);
        System.arraycopy(encryptedBytes, 12, salt, 0, 16);
        System.arraycopy(encryptedBytes, 28, cipherText, 0, cipherText.length);

        SecretKey aesKey = getAESKeyFromPassword(password, salt);

        Cipher cipher = Cipher.getInstance("AES/GCM/NoPadding");
        GCMParameterSpec parameterSpec = new GCMParameterSpec(128, iv); //128 bit auth tag length
        cipher.init(Cipher.DECRYPT_MODE, aesKey, parameterSpec);

        byte[] plainText = cipher.doFinal(cipherText);

        return new String(plainText, StandardCharsets.UTF_8);
    } catch (Exception e) {
        throw new NitriteSecurityException("Error during decryption", e);
