        case Errors.NONE:
            return Optional.empty();
        case Errors.INVALID_REQUEST:
        case Errors.INVALID_RECORD:
        case Errors.UNSUPPORTED_VERSION:
            pushIntervalMs = Integer.MAX_VALUE;
            logger.info("Setting push interval to MAX_VALUE due to invalid request or record or unsupported version");
            break;
        case Errors.UNKNOWN_SUBSCRIPTION_ID:
        case Errors.UNSUPPORTED_COMPRESSION_TYPE:
            pushIntervalMs = 0;
            logger.error("Unknown subscription ID or unsupported compression type");
            break;
        case Errors.TELEMETRY_TOO_LARGE:
        case Errors.THROTTLING_QUOTA_EXCEEDED:
            pushIntervalMs = intervalMs > 0? intervalMs : DEFAULT_INTERVAL_MS;
            logger.error("Telemetry too large or throttling quota exceeded, setting push interval to " + pushIntervalMs);
            break;
        default:
            pushIntervalMs = Integer.MAX_VALUE;
            logger.error("Unknown error code, setting push interval to MAX_VALUE");
            break;
    }
    logger.info("Error code: " + errorCode + ", reason: " + getReason(errorCode) + ", push interval: " + pushIntervalMs);
    return Optional.of(pushIntervalMs);
}
