public static ByteBuffer decompress(byte[] metrics, CompressionType compressionType) throws KafkaException {
    try {
        ByteArrayInputStream bais = new ByteArrayInputStream(metrics);
        InputStream in;
        switch (compressionType) {
            case GZIP:
                in = new GZIPInputStream(bais);
                break;
            case SNAPPY:
                byte[] uncompressed = new byte[Snappy.uncompressedLength(metrics)];
                Snappy.uncompress(metrics, 0, metrics.length, uncompressed, 0);
                return ByteBuffer.wrap(uncompressed);
            case LZ4:
                in = new LZ4BlockInputStream(bais);
                break;
            case ZSTD:
                in = new ZstdInputStream(bais);
                break;
            default:
                throw new KafkaException("Unsupported compression type: " + compressionType);
        }
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        byte[] buffer = new byte[4096];
        int len;
        while ((len = in.read(buffer)) > 0) {
            baos.write(buffer, 0, len);
        }
        in.close();
        return ByteBuffer.wrap(baos.toByteArray());
    } catch (IOException e) {
        throw new KafkaException("I/O error during decompression", e);
    }
}
