public String encrypt(byte[] plainText) throws NitriteSecurityException {
    try {
        SecureRandom secureRandom = new SecureRandom();
        byte[] salt = new byte[16];
        secureRandom.nextBytes(salt);

        SecretKeyFactory factory = SecretKeyFactory.getInstance("PBKDF2WithHmacSHA256");
        KeySpec spec = new PBEKeySpec(password.toCharArray(), salt, 65536, 256);
        SecretKey secret = new SecretKeySpec(factory.generateSecret(spec).getEncoded(), "AES");

        Cipher cipher = Cipher.getInstance("AES/GCM/NoPadding");
        byte[] iv = new byte[12];
        secureRandom.nextBytes(iv);
        GCMParameterSpec parameterSpec = new GCMParameterSpec(128, iv);
        cipher.init(Cipher.ENCRYPT_MODE, secret, parameterSpec);

        byte[] encryptedData = cipher.doFinal(plainText);

        ByteArrayOutputStream outputStream = new ByteArrayOutputStream();
        outputStream.write(iv);
        outputStream.write(salt);
        outputStream.write(encryptedData);

        return Base64.getUrlEncoder().encodeToString(outputStream.toByteArray());
    } catch (Exception e) {
        throw new NitriteSecurityException("Encryption failed", e);
    }
}
