public void process(String base64AuthenticatorData, String signature, String clientDataJson, Fido2RegistrationData registration,
                        Fido2AuthenticationData authenticationEntity) {
    try {
        byte[] authenticatorData = Base64.getDecoder().decode(base64AuthenticatorData);
        byte[] clientDataHash = Hash.sha256(clientDataJson.getBytes(StandardCharsets.UTF_8));
        byte[] rpIdHash = Hash.sha256(registration.getRpId().getBytes(StandardCharsets.UTF_8));

        if (!Arrays.equals(rpIdHash, Arrays.copyOfRange(authenticatorData, 0, 32))) {
            throw new Fido2CompromisedDevice("Registration domain does not match");
        }

        if (authenticatorData[32]!= (byte) 0x01) {
            throw new Fido2CompromisedDevice("User verification option is not valid");
        }

        int counter = ByteBuffer.wrap(Arrays.copyOfRange(authenticatorData, 33, 37)).getInt();
        if (counter <= registration.getCounter()) {
            throw new Fido2CompromisedDevice("Counter is not valid");
        }

        byte[] authenticatorDataToSign = new byte[37];
        System.arraycopy(authenticatorData, 0, authenticatorDataToSign, 0, 37);
        byte[] signatureData = Base64.getDecoder().decode(signature);
        PublicKey publicKey = KeyFactory.getInstance("EC").generatePublic(new X509EncodedKeySpec(registration.getPublicKey()));
        Signature sig = Signature.getInstance("SHA256withECDSA");
        sig.initVerify(publicKey);
        sig.update(authenticatorDataToSign);
        if (!sig.verify(signatureData)) {
            throw new Fido2CompromisedDevice("Assertion signature is not valid");
        }

        registration.setCounter(counter);
    } catch (Exception e) {
        throw new Fido2RuntimeException("Error during FIDO2 authentication process", e);
    }
}
