public static CacheControlHeader fromExpirationTime(String expirationTime) {
    if (expirationTime == null) {
        logger.warn("Expiration time is null, using default CacheControlHeader");
        return new CacheControlHeader();
    }

    CacheControlHeader cacheControlHeader = new CacheControlHeader();
    String[] timeUnits = expirationTime.split(" ");

    for (String timeUnit : timeUnits) {
        try {
            int value = Integer.parseInt(timeUnit.substring(0, timeUnit.length() - 1));
            char unit = timeUnit.charAt(timeUnit.length() - 1);

            switch (unit) {
                case's':
                    cacheControlHeader.setMaxAge(value);
                    break;
                case'm':
                    cacheControlHeader.setMaxAge(value * 60);
                    break;
                case 'h':
                    cacheControlHeader.setMaxAge(value * 60 * 60);
                    break;
                case 'd':
                    cacheControlHeader.setMaxAge(value * 24 * 60 * 60);
                    break;
                default:
                    logger.warn("Invalid time unit: " + unit + ", ignoring this time unit");
                    break;
            }
        } catch (NumberFormatException e) {
            logger.warn("Invalid number format: " + timeUnit + ", ignoring this time unit");
        }
    }

    return cacheControlHeader;
}
