    S3Client s3 = S3Client.builder().region(Region.US_WEST_2).build();

    // Create a ListObjectsV2Request
    ListObjectsV2Request listObjectsV2Request = ListObjectsV2Request.builder()
           .bucket(fileUri.getHost())
           .prefix(fileUri.getPath().substring(1))
           .build();

    // Create a list to store the file URIs
    List<String> fileUris = new ArrayList<>();

    // Use pagination to list all files
    ListObjectsV2Iterable listObjectsV2Iterable = s3.listObjectsV2Paginator(listObjectsV2Request);
    for (ListObjectsV2Response listObjectsV2Response : listObjectsV2Iterable) {
        for (S3Object s3Object : listObjectsV2Response.contents()) {
            fileUris.add(s3Object.key());
        }
    }

    // If recursive is true, list all files in subdirectories
    if (recursive) {
        for (String fileUriString : fileUris) {
            fileUris.addAll(Arrays.asList(listFiles(URI.create(fileUriString), true)));
        }
    }

    // Convert the list to an array and return it
    return fileUris.toArray(new String[0]);
}
