public ListenableFuture<Iterable<Digest>> findMissingBlobs(Iterable<Digest> blobDigests, RequestMetadata requestMetadata) throws IOException {
    if (isRequestInDenyList(requestMetadata)) {
        return Futures.immediateFailedFuture(Status.UNAVAILABLE);
    }

    List<Digest> nonEmptyBlobs = new ArrayList<>();
    for (Digest blobDigest : blobDigests) {
        if (!blobDigest.isEmpty()) {
            nonEmptyBlobs.add(blobDigest);
        }
    }

    if (nonEmptyBlobs.isEmpty()) {
        return Futures.immediateFuture(ImmutableList.of());
    }

    if (serverConfig.isFindMissingBlobsViaBackplaneAllowed()) {
        return findMissingBlobsViaBackplane(nonEmptyBlobs, requestMetadata);
    } else {
        return findMissingBlobsQueryingEachWorker(nonEmptyBlobs, requestMetadata);
    }
}
